function trackCustomEvent(customEvent) {
  var eventname = "/custom-event/site/"+customEvent;
  if (pageTracker != undefined) {
    pageTracker._trackPageview(eventname);
    return true;
  }
}
function trackExternalLink(externalLink) {
  return trackCustomEvent('external-link/'+externalLink.replace("http://", ""));
}

$('a.offsite').click( function() {
  trackExternalLink(this.href);
});
$('a.social').click( function() {
  trackCustomEvent( "social/" + this.name );
});
$('a.trackme').click( function() {
  trackCustomEvent( "link/" + this.name );
});
$('button.trackme').click( function() {
  trackCustomEvent('button/'+$(this).text());
});
$('a.trackme-cross').click( function() {
      trackCustomEvent( "link/" + this.name );
});
