DIV.SCRIPT:
(function () {
    var Y   = YAHOO,
        yut = Y.util,
        yud = yut.Dom,
        yue = yut.Event,
        cmpEl   = yud.get('btn-cmp'),
        expEl   = yud.get('btn-exp'),
        dEls    = yud.getElementsByClassName('details', 'p', 'main'),
        dState  = false,
        rowEls, i, m;

    yud.addClass( cmpEl, 'sel' );

    Y.namespace("SiteExplorer");

    Y.SiteExplorer.showResultDetails = function () {
        yud.setStyle( dEls, 'display', 'block' );
        yud.addClass( expEl, 'sel' );
        yud.removeClass( cmpEl, 'sel' );
        dState = true;
    };

    Y.SiteExplorer.hideResultDetails = function () {
        yud.setStyle( dEls, 'display', 'none' );
        yud.addClass( cmpEl, 'sel' );
        yud.removeClass( expEl, 'sel' );
        dState = false;
    };

    yue.on( cmpEl, 'click', function (e) {
        yue.preventDefault( e );
        if (dState) {
            Y.SiteExplorer.hideResultDetails();
        }
    });

    yue.on( expEl, 'click', function (e) {
        yue.preventDefault( e );
        if (!dState) {
            Y.SiteExplorer.showResultDetails();
        }
    });

    /* set up highlighting for table rows -- for ie6 */
    if (YAHOO.env.ua.ie <= 6) {
        rowEls  = yud.get('result-list').getElementsByTagName('tr');
        m = rowEls.length - 1;
        for (i = 0; i <= m; i+=1) {
            var trEl = rowEls[i];
            yue.on( trEl, 'mouseover', function (e, obj) {
                yud.addClass( obj, 'hover' );
            }, trEl, false);
            yue.on( trEl, 'mouseout', function (e, obj) {
                yud.removeClass( obj, 'hover' );
            }, trEl, false);
        }
    }
}());
